package com.pm.products.client.config;import com.pm.products.client.service.ProductServiceHttpClient;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.reactive.function.client.WebClient;import org.springframework.web.reactive.function.client.support.WebClientAdapter;import org.springframework.web.service.invoker.HttpServiceProxyFactory;@Configurationpublic class GlobalClientConfig {    @Value("${product.service.url}")    private String PRODUCT_SERVICE_URL;    @Bean    public WebClient productServiceClient() {        return WebClient.builder()                .baseUrl(PRODUCT_SERVICE_URL)                .build();    }    @Bean    public ProductServiceHttpClient productService() {        HttpServiceProxyFactory httpServiceProxyFactory =                HttpServiceProxyFactory.                        builder(WebClientAdapter.forClient(productServiceClient()))                        .build();        return httpServiceProxyFactory.createClient(ProductServiceHttpClient.class);    }}