package com.pm.products.client.service;import com.pm.products.client.model.Product;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.service.annotation.GetExchange;import org.springframework.web.service.annotation.HttpExchange;import org.springframework.web.service.annotation.PostExchange;import java.util.List;@HttpExchange("/products")public interface ProductServiceHttpClient {    @GetExchange    public List<Product> getAllProducts();    @PostExchange    public Product addProduct(@RequestBody Product product);    @GetExchange("/{productId}")    public Product getProductById(@PathVariable int productId);}